package dk.emstar.jsonpath;

import com.jayway.jsonpath.DocumentContext;
import com.jayway.jsonpath.JsonPath;

import java.util.List;
import java.util.Map;

public class App {

    public final static String JSON = "{\"book\": [{\"title\": \"Beginning JSON\",\"author\": \"Ben Smith\",\"price\": 49.99},{\"title\": \"JSON at Work\",\"author\": \"Tom Marrs\",\"price\": 29.99},{\"title\": \"Learn JSON in a DAY\",\"author\": \"Acodemy\",\"price\": 8.99},{\"title\": \"JSON: Questions and Answers\",\"author\": \"George Duckett\",\"price\": 6.00}],\"price range\": {\"cheap\": 10.00,\"medium\": 20.00}}";

    public void main() {
        DocumentContext context = JsonPath.parse(JSON);
        List<Map<String, Object>> expensive = context
                .read("$['book'][?(@['price'] > $['price range']['medium'])]");
        return;
    }

    public static void main(String[] args) {
        App app = new App();
        app.main();
    }
}
